import React  from "react";

const AddItem=(state)=>{
const {items,handlechange} = state;

const man =items.map (item=>{
  return (
    <div key={item.id}>
{item.name}
{item.age}
    </div>
  )
})

handleSubmit = (e)=>{
  e.preventDefault();
  return(
    {man}
  )
}



    return (
      <div className="items.id">
 <form onSubmit={handleSubmit}>


 <input type="text" placeholder="enter name" onChange={handlechange} id="name"/>
 <input type="number" placeholder="enter age" onChange={handlechange} id="age" />  
   <input type="submit" placeholder="submit"/>
 </form>
 </div>
 
)
}
export default AddItem;
