import React ,{Component} from 'react';
import AddItem from './additem';
import ToDoList from './todoitems';

class App extends Component{

state = {
  items :[
    {id:1,name:"hazem",age:25},
    {id:2,name:"omar",age:27},
    {id:3,name:"Aly",age:30}

  ]
}
handlechange=(e)=>{
  this.setState({
[e.target.id]:e.target.value 
 })
}

handleSubmit=(e)=>
{
  e.preventDefault();

  let items  = this.state.items;
  items.push("e.target.name","e.target.age");

  const nam = items.map(item=>{
    return (
      <div key={item.id}>
         {item.name}
    {item.age}
      </div>
    )
      } )


 return (

  <div key={items.id}>
   {nam}
  </div>
)
}

  render(){


  return (
    <div className="App">
To Do List App
<ToDoList items={this.state.items} />
<AddItem  handlechange = {this.handlechange} handleSubmit={this.handleSubmit}/>


    </div>
  );
  }
}

export default App;
