const AddItem=(state)=>{
const {handlechange,items} = state;

handleSubmit=(e)=>{
  e.preventDefault();
 this.setState({
  [e.target.id]:e.target.value
})
 
}
const man=items.map(item=>{
  rerurn (
    <div>
      {item.name}
      {item.age}
    </div>
  )
})



    return (
      <div key="items.id">
 <form onSubmit={handleSubmit}>


 <input type="text" placeholder="enter name" onChange={handlechange} id="name"/>
 <input type="number" placeholder="enter age" onChange={handlechange} id="age" />  
   <input type="submit" placeholder="submit" onChange={man}/>
 </form>
 </div>
 
)
}

export default AddItem;
