import './App.css';
import React, { Component } from 'react';
import ToDoList from './todoitems';
import AddItem from './additem';
class App extends Component() {
  state = {
    items :[
      {id:1,age:"hazem",age:25},
      {id:2,age:"omar",age:27},
      {id:3,age:"Aly",age:30}

    ]
  }
  deleteItem=(id)=>{
let items = this.state.items.filter(item=>{
  return item.id !==id
})
this.setState({items})
}
  render(){
  return (
    <div className="App">
To Do List App
<ToDoList items={this.state.items} deleteItem={this.deleteItem}/>
<AddItem items={this.state.items}/>
    </div>
  );
  }
}

export default App;
