//import ToDoList from "./todoitems";
const AddItem =(props)=>{
    const {items} =props;
    let i=2;

    function addItem(i){
   i++;
   items.age=document.getElementById("itemAge").value;
   items.name=document.getElementById("itemName").value;
items.id = i;        
 this.setState({items})

    
    }
return (
    <div className="AddItem">
  <input type="text" id="itemName">ItemName</input>
 <input type="submit" id="itemAge" onClick={addItem}>ItemAge</input>   
         </div>
)
}
export default AddItem;
