import React  from "react";
import ReactDOM from 'react-dom/client';
const AddItem=(state)=>{
const {handlechange,items} = state;



function handleSubmit (){
  e.preventDefault();
  let items = this.state.items;
 
  this.setState({
    items
  })
 
}


    return (
      <div className="items.id">
 <form onSubmit={handleSubmit}>


 <input type="text" placeholder="enter name" onChange={handlechange} id="name"/>
 <input type="number" placeholder="enter age" onChange={handlechange} id="age" />  
   <input type="submit" placeholder="submit"/>
 </form>
 </div>
 
)
}
const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(<handleSubmit />);
export default AddItem;
