class ToDoList extends Component
{
        state = {
                items :[
                  {id:1,name:"hazem",age:25},
                  {id:2,name:"omar",age:27},
                  {id:3,name:"Aly",age:30}
              
                ]
              }
              
              deleteItem=(id)=>{
                let items = this.state.items.filter(item=>{
                  return item.id !==id
                })
                this.setState({items})
                }
        render()
              {

              
     
        return(
                <div className="items.id">
                          <p>{this.state.name}</p>
                          <p>{this.state.age}</p>
                          <button onClick={this.deleteItem}>Delete</button>
  </div>
);
   }
}
