import './App.css';
import React ,{Component} from 'react';
import ToDoList from './todoitems';
import AddItem from './additem';
<script src="https://cdnjs.cloudflare.com/ajax/libs/bootstrap-table/1.16.0/bootstrap-table.min.js"></script>

class App extends Component{

state = {
  items :[
    {id:1,name:"hazem",age:25},
    {id:2,name:"omar",age:27},
    {id:3,name:"Aly",age:30}

  ]
}


  deleteItem=(id)=>{
let items = this.state.items.filter(item=>{
  return item.id !==id
})
this.setState({items})
}
addItem=(item)=>{
  item.id=Math.random();
let items = this.state.items;
items.push(item);
this.setState({items})
}

  render(){
  return (
    <div className="App">
To Do List App
<ToDoList items={this.state.items} deleteItem={this.deleteItem}/>
<AddItem addItem={this.addItem}/>
    </div>
  );
  }
}

export default App;
