import React  from "react";


const AddItem=(state)=>{
const {items} = state;
const add = items.map=(item=>{
  return(
    <div key="item.id">
<p>{item.name}</p>
<p>{item.age}</p>
      </div>
  )      

}
)
handlechange=(e)=>
{
this.setState
({

  [e.target.id]:e.target.value
})}


     
 








    return (
      <div className="items.id">
 <form onSubmit={add} >


 <input type="text" placeholder="enter name" onChange={this.handlechange} id="name"/>
 <input type="number" placeholder="enter age" onChange={this.handlechange} id="age" />  
   <input type="submit" name="add"></input>
 </form>
 </div>
 
)
}
export default AddItem;
