import React  from "react";
import ToDoList from "./todoitems";
const AddItem =(props)=>{
    const {items} =props;
let i=2;

    const addItem =items.map(item=>{
   i++;
   item.age=document.getElementById("itemAge").value;
   item.name=document.getElementById("itemName").value;
item.id = i;
        return (
       
       <div>
       
        <div>

            return(
                this.setState({item});
            )

        </div>
        
        </div>
    
    )
    })
return (
    <div className="AddItem">
  <input type="text" id="itemName">ItemName</input>
        <input type="submit" id="itemAge" onClick={addItem}>ItemAge</input>    </div>
)
}
export default AddItem;
