//import ToDoList from "./todoitems";

class AddItem extends Component{
state = {
  items:[
    {id:1,name:"hazem",age:25},
    {id:2,name:"omar",age:27},
    {id:3,name:"Aly",age:30}


]
}
handlechange=(e)=>
{
this.setState
({

  [e.target.id]:e.target.value
})}


handleSubmit=(e)=>{
  e.preventDefault();

this.setState({
[e.target.name]:e.target.value,

[e.target.age]:e.target.value

});
this.addItem(e);

}

addItem(e)
{
  let items = this.state.items;
items.push(e);
const root = ReactDOM.createRoot(
  document.getElementById('root')
);root.render(items);
}
render(){
    return (
      <div className="items.id">
 <form onSubmit={this.handleSubmit} >


 <input type="text" placeholder="enter name" onChange={this.handlechange} id="name"/>
 <input type="number" placeholder="enter age" onChange={this.handlechange} id="age" />  
   <input type="submit" name="add"/>
 </form>
 </div>
 
);
}}
export default AddItem;
