    i++;
    item.age=document.getElementById("itemAge").value;
    item.name=document.getElementById("itemName").value;
    item.id=i; 
    return (
    this.setState({items})
    
    )
    })
    return (
    <div className="items.id">
  <input type="text" id="itemName">ItemName</input>
 <input type="submit" id="itemAge">ItemAge</input>   
         <button onClick={addItem(items.id)} type="submit">Add</button>
         </div>
)
}
