    {id:3,name:"Aly",age:30}


]
}
handlechange1=(e)=>
{
this.setState
({

  [e.target.id]:e.target.value,
})}


handleSubmit=(e)=>{
  e.preventDefault();


  const add = items.map=(item=>{
    return(
      <div key="item.id">
<p>{item.name}</p>
<p>{item.age}</p>
        </div>
    )      

}
  )       
}






render(){
