import './App.css';
import React, { Component } from 'react';
import ToDoList from './todoitems';
import AddItem from './additem';
let i = 3;

class App extends Component() {
  state = {
    items :[
      {id:1,name:"hazem",age:25},
      {id:2,name:"omar",age:27},
      {id:3,name:"Aly",age:30}

    ]
  }
  deleteItem=(id)=>{
let items = this.state.items.filter(item=>{
  return item.id !==id
})
this.setState({items})
}

 addItem=(items)=>{
i++;
items.age=document.getElementById("itemAge").value;
items.name=document.getElementById("itemName").value;
items.id=i; 
return(       
this.setState({items})
)

}
  render(){
  return (
    <div className="App">
To Do List App
<ToDoList items={this.state.items} deleteItem={this.deleteItem}/>
<AddItem  items={this.state.items} addItem={this.addItem} />
    </div>
  );
  }
}

export default App;
