import './App.css';
import React ,{Component} from 'react';
import AddItem from './additem';
import ToDoList from './todoitems';

class App extends Component{

state = {
  items :[
    {id:1,name:"hazem",age:25},
    {id:2,name:"omar",age:27},
    {id:3,name:"Aly",age:30}

  ]
}
handlechange=(e)=>{
  e.preventDefault();
  this.setState({
[e.target.id]:e.target.value 
 })
}

handleSubmit =(e)=>{
  e.preventDefault();
      return (
      <div key={e.target.id}>
  {e.target.name}
  {e.target.age}
      </div>
    )

}

  render(){


  return (
    <div className="App">
To Do List App
<ToDoList items={this.state.items} />
<AddItem items= {this.state.items} handlechange = {this.handlechange} handleSubmit={this.handleSubmit}/>


    </div>
  );
  }
}

export default App;
